import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const formatDate = (date) => {
    return date.toLocaleDateString('en-GB', {
      day: 'numeric',
      month: 'long',
      year: 'numeric',
    });
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: true,
    }) + ' IST';
  };

  return (
    <div className="flex flex-col w-full font-sans">
      {/* Top Bar - Dark Maroon */}
      <div className="bg-[#4a1d1d] text-white text-xs py-1 px-4 md:px-8 flex justify-between items-center">
        <div className="flex items-center gap-4">
          <span>{formatDate(currentTime)} | {formatTime(currentTime)}</span>
        </div>
        <div className="flex items-center gap-4">
          <span className="hidden md:inline cursor-pointer hover:underline">Screen Reader Access</span>
          <span className="hidden md:inline">|</span>
          <span className="cursor-pointer hover:underline">Skip to main content</span>
          <span className="hidden md:inline">|</span>
          <div className="flex gap-2">
            <span className="cursor-pointer hover:text-gray-300 font-bold">A-</span>
            <span className="cursor-pointer hover:text-gray-300 font-bold">A</span>
            <span className="cursor-pointer hover:text-gray-300 font-bold">A+</span>
          </div>
          <span className="hidden md:inline">|</span>
          <div className="flex gap-2">
            <i className="fab fa-facebook-f cursor-pointer hover:text-gray-300"></i>
            <i className="fab fa-twitter cursor-pointer hover:text-gray-300"></i>
            <i className="fab fa-youtube cursor-pointer hover:text-gray-300"></i>
          </div>
        </div>
      </div>

      {/* Main Banner - Terracotta Red */}
      <div className="bg-[#be3a34] text-white py-6 px-4 md:px-8 flex flex-col md:flex-row items-center justify-between shadow-md relative overflow-hidden">
        {/* Texture Overlay (Optional subtle pattern) */}
        <div className="absolute inset-0 opacity-10 pointer-events-none" style={{ backgroundImage: 'url("https://www.transparenttextures.com/patterns/aged-paper.png")' }}></div>

        <div className="flex items-center gap-6 z-10 w-full justify-between md:justify-start">
          <img
            src="https://nitc.ac.in/xc-assets/logo/nitc_logo_icon.svg"
            alt="NITC Logo"
            className="w-20 h-20 md:w-24 md:h-24 bg-white rounded-full p-1 shadow-lg"
          />
          <div className="flex flex-col text-left">
            <h1 className="text-2xl md:text-3xl font-bold tracking-wide uppercase drop-shadow-sm">
              SNS Club
            </h1>
            <h2 className="text-sm md:text-lg font-medium opacity-90 tracking-wider">
              National Institute of Technology Calicut
            </h2>
            <div className="h-0.5 w-1/3 bg-white/50 mt-2"></div>
            <p className="text-xs md:text-sm mt-1 italic opacity-80">Bridging Science & Spirituality</p>
          </div>

          {/* Mobile Menu Button - Visible only on small screens */}
          <button className="md:hidden text-2xl text-white ml-auto">
            â˜°
          </button>
        </div>

        {/* Right side Logo placeholder (Government/Ministry style) */}
        <div className="hidden md:flex items-center gap-4 z-10">
          <div className="flex flex-col items-end text-right">
            <span className="text-xs uppercase font-bold">Ministry of Education</span>
            <span className="text-xs uppercase">Government of India</span>
          </div>
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/5/55/Emblem_of_India.svg"
            alt="Emblem of India"
            className="w-12 h-16 invert brightness-0 saturate-100 filter drop-shadow-sm"
            style={{ filter: 'brightness(0) invert(1)' }}
          />
        </div>
      </div>

      {/* Navigation Bar - Dark Brown/Black */}
      <nav className="bg-[#2d1b1b] text-white px-4 md:px-8 border-t-4 border-[#d4af37] shadow-lg sticky top-0 z-50">
        <ul className="hidden md:flex flex-wrap items-center justify-center md:justify-start gap-px">
          {[
            { name: 'HOME', path: '/home' },
            { name: 'ABOUT US', path: '/overview' },
            { name: 'SCIENCE', path: '/science' },
            { name: 'SPIRITUALITY', path: '/spirituality' },
            { name: 'GALLERY', path: '/gallery' },
            { name: 'EVENTS', path: '/events' },
            { name: 'CONTACT', path: '/contact' } // Added contact for completeness
          ].map((item, index) => (
            <li key={index} className="flex-1 md:flex-none">
              <Link
                to={item.path}
                className="block py-3 px-6 text-sm font-bold tracking-wider hover:bg-[#3e2a2a] transition-colors border-r border-[#3e2a2a]/30 uppercase text-center"
              >
                {item.name}
              </Link>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
};

export default Header;
