import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Index.css';

const Index = () => {


  const videos = [
    { src: 'videos/video1.mp4', title: 'People of this country', description: 'Hi I am Rahul Kumar Singh, I am studying in NIT Calicut.' },
    { src: 'videos/video2.mp4', title: 'Truth and Reality', description: 'Nobody cares about you until you care about yourself.' },
    { src: 'videos/video3.mp4', title: 'Everybody watches you', description: 'I try to watch myself but still do what I want.' }
  ];



  return (
    <div className="index-page">
      <header className="flex flex-col md:flex-row justify-between items-center mb-10 pb-4 border-b-2 border-amber-900/20 px-2">
        <div className="content-text px-2">
          <img src="https://nitc.ac.in/xc-assets/logo/nitc_logo_icon.svg" alt="NITC Logo" className="w-18 h-14" />
          <h3 className="text-2xl md:text-1xl">
            NATIONAL INSTITUTE OF TECHNOLOGY CALICUT
          </h3>
        </div>

        <div className="flex items-center justify-center gap-4 bg-black-400 text-white font-semibold text-2lg md:text-xl px-8 py-4">
          <nav className="flex flex-wrap gap-x-4 gap-y-2 text-lg parchment-text content-text">
            <Link to="/home">Home</Link>
            <Link to="/science">Science</Link>
            <Link to="/spirituality">Spirituality</Link>
            <Link to="/gallery">Gallery</Link>
            <Link to="/events">Events</Link>
            <Link to="/overview">Overview</Link>
          </nav>
        </div>
      </header>

      <section className="relative h-[300px] overflow-hidden flex items-center justify-center bg-black">
        <div id="video-slider" className="relative w-full h-full flex items-center justify-center px-8">
          {videos.map((video, index) => (
            <div key={index} className={`video-slide-wrapper ${index === 0 ? 'active' : ''}`}>
              <video src={video.src} className="video-slide" autoPlay loop muted />
              <div className="video-info">
                <h3>{video.title}</h3>
                <p>{video.description}</p>
              </div>
            </div>
          ))}
        </div>
        <button id="prevBtn" className="absolute left-4 top-1/2 transform -translate-y-1/2 z-20 bg-black/50 hover:bg-black/70 p-3 rounded-full text-white">
          &#10094;
        </button>
        <button id="nextBtn" className="absolute right-4 top-1/2 transform -translate-y-1/2 z-20 bg-black/50 hover:bg-black/70 p-3 rounded-full text-white">
          &#10095;
        </button>
      </section>


    </div>
  );
};

export default Index;
