import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Index.css';

const Index = () => {
  const [currentVideo, setCurrentVideo] = useState(0);

  const videos = [
    { src: 'videos/video1.mp4', title: 'People of this country', description: 'Hi I am Rahul Kumar Singh, I am studying in NIT Calicut.' },
    { src: 'videos/video2.mp4', title: 'Truth and Reality', description: 'Nobody cares about you until you care about yourself.' },
    { src: 'videos/video3.mp4', title: 'Everybody watches you', description: 'I try to watch myself but still do what I want.' }
  ];

  // Auto-advance slider
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentVideo((prev) => (prev + 1) % videos.length);
    }, 6000);
    return () => clearInterval(timer);
  }, [videos.length]);

  const nextVideo = () => {
    setCurrentVideo((prev) => (prev + 1) % videos.length);
  };

  const prevVideo = () => {
    setCurrentVideo((prev) => (prev === 0 ? videos.length - 1 : prev - 1));
  };

  return (
    <div className="index-page bg-[#faf9f6] min-h-screen text-gray-800 font-sans flex flex-col">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-white shadow-sm border-b border-gray-100 transition-all duration-300">
        <div className="container mx-auto px-4 py-3 flex flex-col md:flex-row justify-between items-center">
          {/* Logo Section */}
          <div className="flex items-center gap-4 mb-4 md:mb-0 group cursor-pointer">
            <img src="https://nitc.ac.in/xc-assets/logo/nitc_logo_icon.svg" alt="NITC Logo" className="w-12 h-12 md:w-14 md:h-12 opacity-90 group-hover:opacity-100 transition-opacity" />
            <div className="flex flex-col">
              <h3 className="text-lg md:text-xl font-bold text-gray-800 tracking-tight leading-none group-hover:text-orange-600 transition-colors">
                NATIONAL INSTITUTE OF TECHNOLOGY
              </h3>
              <p className="text-xs text-gray-500 font-medium tracking-wide">CALICUT</p>
            </div>
          </div>

          {/* Navigation */}
          <nav className="flex flex-wrap justify-center gap-x-6 md:gap-x-8 gap-y-2 text-base font-medium text-gray-600">
            {['Home', 'Science', 'Spirituality', 'Gallery', 'Events', 'Overview'].map((item) => (
              <Link
                key={item}
                to={`/${item.toLowerCase()}`}
                className="nav-link relative hover:text-orange-600 transition-colors py-2"
              >
                {item}
              </Link>
            ))}
          </nav>
        </div>
      </header>

      {/* Hero Section / Video Slider */}
      <section className="relative h-[500px] md:h-[700px] w-full overflow-hidden bg-gray-100 group">
        <div id="video-slider" className="relative w-full h-full">
          {videos.map((video, index) => (
            <div
              key={index}
              className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${index === currentVideo ? 'opacity-100 z-10' : 'opacity-0 z-0'}`}
            >
              <video
                src={video.src}
                className="w-full h-full object-cover"
                autoPlay
                loop
                muted
                playsInline
              />
              {/* Overlay Gradient */}
              <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-black/20 to-transparent"></div>

              {/* Content */}
              <div className="absolute bottom-16 md:bottom-24 left-4 md:left-20 max-w-3xl text-white p-6 md:p-10 fade-in">
                <h3 className="text-3xl md:text-5xl font-bold mb-4 drop-shadow-lg tracking-wide">{video.title}</h3>
