import React from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const Gallery = () => {
  const galleryImages = [
    {
      src: 'https://placehold.co/600x400/81b29a/ffffff?text=Stargazing+Night',
      alt: 'A stargazing event with telescopes.',
      caption: 'Our first stargazing night, looking towards Andromeda.'
    },
    {
      src: 'https://placehold.co/600x400/e07a5f/ffffff?text=Guest+Lecture',
      alt: 'Guest speaker at a lecture.',
      caption: 'Dr. Sharma\'s engaging talk on neuroplasticity.'
    },
    {
      src: 'https://placehold.co/600x400/f2cc8f/3d405b?text=Discussion+Circle',
      alt: 'Members in a discussion circle.',
      caption: 'A lively discussion on the nature of consciousness.'
    },
    {
      src: 'https://placehold.co/600x400/3d405b/ffffff?text=Nature+Walk',
      alt: 'Group on a contemplative nature walk.',
      caption: 'Finding patterns in nature during our mindfulness walk.'
    },
    {
      src: 'https://placehold.co/600x400/6a9e87/ffffff?text=Community+Meal',
      alt: 'Members sharing a meal.',
      caption: 'Breaking bread and sharing ideas after a meeting.'
    },
    {
      src: 'https://placehold.co/600x400/d46a50/ffffff?text=Quiet+Reflection',
      alt: 'A moment of quiet reflection.',
      caption: 'A moment of quiet reflection at our annual retreat.'
    }
  ];

  return (
    <div className="gallery-page scroll-smooth">
      <Header navLinks={navLinks} />

      <main>
        {/* Hero Section */}
        <section className="hero-bg-gallery py-32">
          <div className="container mx-auto px-6 text-center">
            <h1 className="text-5xl md:text-7xl font-bold text-[#3d405b] mb-4">
              Moments of Discovery.
            </h1>
            <p className="text-xl md:text-2xl text-gray-700 max-w-3xl mx-auto">
              A collection of shared experiences from our journey together, capturing the spirit of our community in inquiry and wonder.
            </p>
          </div>
        </section>

        {/* Founder's Note on Community */}
        <section className="py-20 bg-white">
          <div className="container mx-auto px-6 max-w-3xl text-center">
            <h2 className="text-3xl font-bold mb-4">The Heart of Our Club</h2>
            <p className="text-lg text-gray-800 leading-relaxed">
              "When I first envisioned the SNS Club, it wasn't just about ideas; it was about people. It was about creating a space where we could share not just our thoughts, but our experiences. This gallery is a testament to that vision. Each photo holds a storyâ€”of a question asked, a friendship formed, a moment of quiet awe. This is the living, breathing heart of our community."
            </p>
            <p className="text-gray-500 mt-4">- [Your Name], Founder</p>
          </div>
        </section>

        {/* The Gallery Grid */}
        <section className="py-24">
          <div className="container mx-auto px-6">
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {galleryImages.map((image, index) => (
                <div key={index} className="gallery-item bg-white rounded-lg overflow-hidden shadow-md">
                  <img
                    src={image.src}
                    alt={image.alt}
                    className="w-full h-64 object-cover"
                  />
                  <div className="p-4">
                    <p className="text-center text-gray-600 italic">{image.caption}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* Share Your Moments CTA */}
        <section id="join" className="py-24 bg-[#3d405b]">
          <div className="container mx-auto px-6 text-center">
            <h2 className="text-4xl font-bold text-white mb-4">This Is Our Story. Help Us Tell It.</h2>
            <p className="text-xl text-gray-300 max-w-2xl mx-auto mb-8">
              Have a photo from an SNS Club event that captures a moment of connection or wonder? We'd love to see it. Share your moments with us and help grow our collective memory.
            </p>
            <a
              href="mailto:gallery@snsclub.org"
              className="inline-block bg-[#81b29a] text-white px-8 py-4 rounded-full text-lg font-semibold hover:bg-[#6a9e87] transition-colors"
            >
              Submit Your Photo
            </a>
          </div>
        </section>
      </main>

      <Footer />
    </div>
  );
};

export default Gallery;
